#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="$basedir\E:\uniappProject\yammusicplayer\node_modules\.store\@stencil+core@4.14.1\node_modules\@stencil"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="$basedir/E:/uniappProject/yammusicplayer/node_modules/.store/@stencil+core@4.14.1/node_modules/@stencil"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$env_node_path$pathsep$new_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../../../../../@stencil+core@4.14.1/node_modules/@stencil/core/bin/stencil" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../../../../../@stencil+core@4.14.1/node_modules/@stencil/core/bin/stencil" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../../../../../@stencil+core@4.14.1/node_modules/@stencil/core/bin/stencil" $args
  } else {
    & "node$exe"  "$basedir/../../../../../@stencil+core@4.14.1/node_modules/@stencil/core/bin/stencil" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
